// import { setUserSession } from './Utils/Common';
import Axios from "axios";
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';







 
function PostForm(props) {
    const navigate = useNavigate();
    
   const url = "https://localhost:7079/api/User/login"
   const[data ,setData] = useState({
       email:"",
       password:""
   })
   function submit(e){
        e.preventDefault();
       Axios.post(url,{
           email:data.email,
           password:data.password
       }).then(res=>{
           console.log(res.data)    
           navigate('/home'); 
                                            
       }).catch(function(error){
           if(error.response){
               console.log(error.response.data);
               console.log(error.response.status);
               console.log(error.response.headers);
           }
       })
       
       

    
   }
   function handle(e){
       const newdata = {...data}
       newdata[e.target.id]=e.target.value
       setData(newdata)
       console.log(newdata)


   }
   
   return(
    <div className="card fat">
        <div className="card-body">
            <h4 className="card-title">Oturum Aç</h4>
            <form  onSubmit={(e)=>submit(e)} className="my-login-validation" novalidate="">
                    <div className="form-group">
                        <label htmlFor="email">E-Mail Address</label>
                        <input onChange={(e)=> handle(e)} id="email" type="email" className="form-control" name="email" value={data.email} required autofocus/>
                        <div className="invalid-feedback">
                            Email is invalid
                        </div>
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password
                            <a href="forgot.html" className="float-right">
                                Forgot Password?
                            </a>
                        </label>
                        <input onChange={(e)=> handle(e)}  id="password" type="password" className="form-control" name="password" value={data.password} required data-eye />
                        <div className="invalid-feedback">
                            Password is required
                        </div>
                    </div>
                    <div className="form-group m-0">
                        <button type="submit" className="btn btn-primary btn-block">
                            Oturum Aç
                        </button>
                    </div>
                    <div className="mt-4 text-center">
                        Workup 'ta Yeni misiniz? <a href="register.html">Hemen Katılım</a>
                    </div>
            
            </form>
        </div>
	</div>
   )
}

 
export default PostForm;
