// import { setUserSession } from './Utils/Common';
import Axios from "axios";
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';







 
function PostForm(props) {
    const navigate = useNavigate();
    
   const url = "https://localhost:7079/api/User/login"
   const[data ,setData] = useState({
       email:"",
       password:""
   })
   function submit(e){
        e.preventDefault();
       Axios.post(url,{
           email:data.email,
           password:data.password
       }).then(res=>{
           console.log(res.data)    
           navigate('/home'); 
                                            
       }).catch(function(error){
           if(error.response){
               console.log(error.response.data);
               console.log(error.response.status);
               console.log(error.response.headers);
           }
       })
        
   }
   function handle(e){
       const newdata = {...data}
       newdata[e.target.id]=e.target.value
       setData(newdata)
       console.log(newdata)

   }
   
   return(
       <div className="my-login-page">
    <section class="h-100">
        <div class="container h-100">
            <div class="row justify-content-md-center h-100">
                <div className="card-wrapper">
                    <div className="brand">
                        <img src="/images/login-logo.svg" alt="logo"/>
                    </div>
                    <div className="card fat">
                        <div className="card-body">
                            <h4 className="card-title">Oturum Aç</h4>
                            <form  onSubmit={(e)=>submit(e)} className="my-login-validation" novalidate="">
                                    <div className="form-group">
                                        <label htmlFor="email">E-posta Adresi</label>
                                        <input onChange={(e)=> handle(e)} id="email" type="email" className="form-control" name="email" value={data.email} required autofocus/>
                                        <div className="invalid-feedback">
                                            E-posta Zorunludur!!
                                        </div>
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="password">Şifre
                                            <a href="forgot.html" className="float-right">
                                                Şifreni mi Unuttun?
                                            </a>
                                        </label>
                                        <input onChange={(e)=> handle(e)}  id="password" type="password" className="form-control" name="password" value={data.password} required data-eye />
                                        <div className="invalid-feedback">
                                            Şifre Zorunludur!!
                                        </div>
                                    </div>
                                    <div className="form-group m-0">
                                        <button type="submit" className="btn btn-primary btn-block">
                                            Oturum Aç
                                        </button>
                                    </div>
                                    <div className="mt-4 text-center">
                                        Workup 'ta Yeni misiniz? <a href="register.html">Hemen Katılın!</a>
                                    </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
		</div>
	</section>
    </div>
   )
}

 
export default PostForm;
