    login(){
        let obj={}
        obj.email = this.state.email;
        obj.password=this.state.password
        fetch('',
        {
            header:{
                "Content-Type":"application/json"
            },
            method:'POST',
            body:JSON.stringify({obj})
        }
        )
    }
  render() {
    return (
      <div>
          <input type="text" placeholder="Enter Email" onClick={(text)=> {this.handleEmail(text)}}/>
          <br/>
          <input type="password" placeholder='Enter password' onClick={(text)=>{this.handlePass(text)}} />
          <br/>
          <button onClick={()=>{this.login()}}>Login</button>
      </div>
    )
  }
}
