import React, { Component } from 'react';
import styled from "styled-components";

export default class  extends Component {
    constructor(prop){
        super(props);
        this.state={
            isVisible:false,
        }
    }
    onClickEvent=()=>{
        this.setState({
            isVisible:!this.state.isVisible,
        })
    }
    handleFormSubmit=(event)=>{
        event.preventDefault();
        console.log("Search form submitted");
      }
  render() {  
    return (
    <Search>
       <div className="search">
            <div className="searchInputs">
              <input
                type="text"
                onChange={this.props.searchUserCompanyProp} placeholder="Arama Yap..."
                onClick={this.onClickEvent}
              />
            </div>
            {isVisible ? <div className="dataResult">
                {this.props.usersandcompanies.map((usercompany,key) => {
                  return (
                    <a className="dataItem" href={usercompany.girisId} target="_blank">
                      <p>{usercompany.name} </p>
                    </a>
                  );
                })}
              </div>:null}
              
            )}
          </div>   
          </Search> 
 
    )
  }
}
const Search = styled.div`
  opacity: 1;
  flex-grow: 1;
  position: relative;
  & > div {
    max-width: 280px;
    input {
      border: none;
      box-shadow: none;
      background-color: #eef3f8;
      border-radius: 2px;
      color: rgba(0, 0, 0, 0.9);
      width: 218px;
      padding: 0 8px 0 40px;
      line-height: 1.75;
      font-weight: 400;
      font-size: 14px;
      height: 34px;
      border-color: #dce6f1;
      vertical-align: text-top;
    }
  }
`;
