// import { setUserSession } from './Utils/Common';
import Axios from "axios";
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from "styled-components";

function PostForm(props) {
    const navigate = useNavigate();
    
   const url = "https://localhost:7079/api/User/login"
   const[data ,setData] = useState({
       email:"",
       password:""
   })
   function submit(e){
        e.preventDefault();
       Axios.post(url,{
           email:data.email,
           password:data.password
       }).then(res=>{
           console.log(res.data)    
           navigate('/home'); 
                                            
       }).catch(function(error){
           if(error.response){
               console.log(error.response.data);
               console.log(error.response.status);
               console.log(error.response.headers);
           }
       })
         
   }
   function handle(e){
       const newdata = {...data}
       newdata[e.target.id]=e.target.value
       setData(newdata)
       console.log(newdata)

   }
   
   return(
    <Container>
    <Nav>
      <a href="/">
        <img src="/images/login-logo.svg" alt="" />
      </a>
    </Nav>
    <Section>
    <div>
           <form  onSubmit={(e)=>submit(e)}>
               <input onChange={(e)=> handle(e)} id="email" value={data.email} placeholder="name" type="text"></input>
               <input onChange={(e)=> handle(e)} id="password" value={data.password} placeholder="name" type="text"></input>
               <button>submit</button>
           </form>
           
       </div>
    </Section>
  </Container>

      
   )
}

const Container = styled.div`
padding: 0px;
`;

const Nav = styled.nav`
max-width: 1128px;
margin: auto;
padding: 12px 0 16px;
display: flex;
align-items: center;
position: relative;
justify-content: space-between;
flex-wrap: nowrap;

& > a {
  width: 135px;
  height: 34px;
  @media (max-width: 768px) {
    padding: 0 5px;
  }
}
`;

const Section = styled.section`
display: flex;
align-content: start;
min-height: 700px;
padding-bottom: 138px;
padding-top: 40px;
padding: 60px 0;
position: relative;
flex-wrap: wrap;
width: 100%;
max-width: 1000px;
align-items: center;
margin: auto;

@media (max-width: 768px) {
  margin: auto;
  min-height: 0px;
}
`;
export default PostForm;
