// Extends=> component clasısındaki tüm özellikleri collapse classına miras verir
// Render metodu Component classının bir metodudur
//Component olusurken ilk calısacak metod constructor oldugundan Stateleri constructorda yazabiliriz
// Propslar immutable yani degistirilemezler.Stateler ise mutable yani degistirilebilir.

export  class Collapse extends Component {
  constructor(){
    super();
    this.state = {
      showContent:"Card Acik",
      isOpen:true,
    };
    //this.showMore=this.showMore.bind(this); //showmore thisi çözümleyemedigi icin kolay bi yol
  }
  // functional fonksiyon yazdıgımızda this keywordu tanımlayamadıgımdan setState metodunu kullanamıyoruz.Bu durumun düzelmesi için constructora this metodunu bind yöntemiyle tanıtmamız gerekiyor.
  // showMore(){
  //   this.setState=({
  //     isOpen:false,
  //     showContent:"Card Kapali",
  //   });
  // }

  // Arrow function tanımlayamadıgı keyword icin bir ust scope'a giderek this keywordunu orada tanımlayıp kullanabiliyor.
  // State metodu ile olan bir state e yeni bir deger atamak istedigimizde js tarafında sikinti cikmadan calisabiliyor.Ancak react tarafı state komutuyla render islemini ikinci kez tetikleyemediginden state guncellemesini yapamıyor.Bu işlem için setState metodu kullanmalıyız çünkü bu metod çalıstıgında render metodu da tetikleniyor.
  showMore = () => {
    this.setState({
      isOpen:!this.state.isOpen,
      showContent:"Card Kapali",
    });
  };
  render() {
    return (
      <div>
        <p>
          <button
            className="btn btn-primary w-100" onClick={this.showMore}
            
          >
            {this.props.children.props.cardTitle}
          </button>
  
          
        </p>
        <div className="collapse show">
           {this.props.children}
           {this.state.showContent}
        </div>
      </div>
    );
  }
}
