// import { setUserSession } from './Utils/Common';
import Axios from "axios";
import { createBrowserHistory } from "history";
import React, { useState } from 'react';
import styled from "styled-components";

const history=createBrowserHistory();


 
function PostForm(props) {
   const url = "https://localhost:7079/api/User/login"
   const[data ,setData] = useState({
       email:"",
       password:""
   })
   function submit(e){
       e.preventDefault();
       Axios.post(url,{
           email:data.email,
           password:data.password
       }).then(res=>{
           console.log(res.data)
           
           
       })

    
   }
   function handle(e){
       const newdata = {...data}
       newdata[e.target.id]=e.target.value
       setData(newdata)
       console.log(newdata)


   }
   
   return(
       <div>
           <form  onSubmit={(e)=>submit(e)}>
               <input onChange={(e)=> handle(e)} id="email" value={data.email} placeholder="name" type="text"></input>
               <input onChange={(e)=> handle(e)} id="password" value={data.password} placeholder="name" type="text"></input>
               <button>submit</button>
           </form>
       </div>
   )
}
const SignIn = styled.a`
  box-shadow: inset 0 0 0 1px #0a66c2;
  color: #0a66c2;
  border-radius: 24px;
  transition-duration: 167ms;
  font-size: 16px;
  font-weight: 600;
  line-height: 40px;
  padding: 10px 24px;
  text-align: center;
  background-color: rgba(0, 0, 0, 0);
  &:hover {
    background-color: rgba(112, 181, 249, 0.15);
    color: #0a66c2;
    text-decoration: none;
  }
`;
 
export default PostForm;
