// Extends=> component clasısındaki tüm özellikleri collapse classına miras verir
// Render metodu Component classının bir metodudur
//Component olusurken ilk calısacak metod constructor oldugundan Stateleri constructorda yazabiliriz
// Propslar immutable yani degistirilemezler.Stateler ise mutable yani degistirilebilir.

export  class Collapse extends Component {
  constructor(){
    super();
    this.state = {
      showContent:"Card Acik",
      isOpen:true,
    };
    this.showMore=this.showMore.bind(this); //showmore thisi çözümleyemedigi icin kolay bi yol
  }
  showMore(){
    this.setState=({
      isOpen:false,
      showContent:"Card Kapali",
    });
  }
  render() {
    return (
      <div>
        <p>
          <button
            className="btn btn-primary w-100" onclick={this.showMore}
            
          >
            {this.props.children.props.cardTitle}
          </button>
  
          
        </p>
        <div className="collapse show">
           {this.props.children}
           {this.state.showContent}
        </div>
      </div>
    );
  }
}
