import React, { Component } from 'react';
import styled from "styled-components";
import { isVisible } from "@testing-library/user-event/dist/utils";
export default class  extends Component {
    constructor(props){
        super(props);
        this.state={
            isVisible:false,
        }
    }
 
    onClickEvent=()=>{
        this.setState({
            isVisible:!this.state.isVisible,
        });
    }
   
    handleFormSubmit=(event)=>{
        event.preventDefault();
        console.log("Search form submitted");
      }
  render() {  
      const {isVisible}=this.state;
      console.log(isVisible);
    return (
    <Search onClick={this.onClickEvent}>
       <div className="search">
            <div className="searchInputs">
              <input
                type="text"
                onChange={this.props.searchUserCompanyProp} placeholder="Arama Yap..."
                onClick={this.onClickEvent}
              />
            </div>
            
            {
                isVisible ?
            
                <SignOut>
                    {this.props.usersandcompanies.map((usercompany,key) => {
                    return (
                        <a href={usercompany.girisId} target="_blank">
                        <p>{usercompany.name} </p>
                        </a>
                    );
                    })}
               </SignOut>  :null
              
            }  
            
           
          </div>   
    </Search> 
 
    )
  }
}
const Search = styled.div`
  opacity: 1;
  flex-grow: 1;
  position: relative;
  & > div {
    max-width: 280px;
    input {
      border: none;
      box-shadow: none;
      background-color: #eef3f8;
      border-radius: 2px;
      color: rgba(0, 0, 0, 0.9);
      width: 218px;
      padding: 0 8px 0 40px;
      line-height: 1.75;
      font-weight: 400;
      font-size: 14px;
      height: 34px;
      border-color: #dce6f1;
      vertical-align: text-top;
    }
  }
`;
const SignOut = styled.div`
  position: absolute;
  top: 45px;
  background: white;
  border-radius: 0 0 5px 5px;
  width: 100px;
  height: 40px;
  font-size: 16px;
  transition-duration: 167ms;
  text-align: center;
  display: none;
`;
