// import { setUserSession } from './Utils/Common';
import Axios from "axios";
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from "styled-components";

function PostForm(props) {
    const navigate = useNavigate();
    
   const url = "https://localhost:7079/api/User/login"
   const[data ,setData] = useState({
       email:"",
       password:""
   })
   function submit(e){
        e.preventDefault();
       Axios.post(url,{
           email:data.email,
           password:data.password
       }).then(res=>{
           console.log(res.data)    
           navigate('/home'); 
                                            
       }).catch(function(error){
           if(error.response){
               console.log(error.response.data);
               console.log(error.response.status);
               console.log(error.response.headers);
           }
       })
         
   }
   function handle(e){
       const newdata = {...data}
       newdata[e.target.id]=e.target.value
       setData(newdata)
       console.log(newdata)

   }
   
   return(
        <div className="card fat">
            <div className="card-body">
                <h4 className="card-title">Login</h4>
                <form className="my-login-validation">
                    <div className="form-group">
                        <label htmlFor="email">E posta</label>
                        <input id="email" type="email" className="form-control" name="email" value="" required autofocus />
                        {/* <div class="invalid-feedback">
                            Email is invalid
                        </div> */}
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Şifre
                            {/* <a href="forgot.html" class="float-right">
                                Forgot Password?
                            </a> */}
                        </label>
                        <input id="password" type="password" className="form-control" name="password" required data-eye />
                        {/* <div class="invalid-feedback">
                                Password is required
                        </div> */}
                    </div>
                    {/* <div class="form-group">
                        <div class="custom-checkbox custom-control">
                            <input type="checkbox" name="remember" id="remember" class="custom-control-input">
                            <label for="remember" class="custom-control-label">Remember Me</label>
                        </div>
                    </div> */}
                    <div className="form-group m-0">
                        <button type="submit" className="btn btn-primary btn-block">
                            Oturum Aç
                        </button>
                    </div>
                    <div className="mt-4 text-center">
                        Workup 'ta Yeni misiniz? <a href="register.html">Hemen Katılın</a>
                    </div>
                </form>
            </div>
        </div>
    )
}

